mapper.select:
SELECT book_no, book_title, book_author, book_publisher, book_category_no, book_category_name, book_pub_year, book_isbn, book_url FROM Book WHERE use_flag = 'Y'
AND book_title LIKE CONCAT('%', #{search}, '%')
limit #{currentPage}, #{recordPage}

mapper.delete:
DELETE FROM
  book_basket
WHERE
  mem_no = #{memNo}
  AND book_no = #{bookNo}
