mapper.select:
SELECT mem_no, mem_name, mem_id, mem_phone, mem_birth, reg_date, mem_rent_yn FROM Member WHERE use_flag = 'Y'
AND ${column} LIKE CONCAT('%', #{search}, '%')
limit #{currentPage}, #{recordPage}

SELECT rent.book_no, book.book_title, rent.mem_no, date_format(rent.rent_date, '%Y.%m.%d') as rent_date, date_format(rent.return_date, '%Y.%m.%d') as return_date, rent.return_state FROM rent JOIN book on rent.book_no = book.book_no WHERE rent.use_flag = 'Y' AND rent.mem_no = #{memNo}
AND ${column} LIKE CONCAT('%', #{search}, '%')
limit #{currentPage}, #{recordPage}
